import mc from "./fight-scene.module.scss";
import { useSelector, useDispatch } from "react-redux";

const FightScene = () => {
  const dispatch = useDispatch;
  const { fightStart } = useSelector((store) => {
    return store.modificatorsReducer;
  });

  const onEndFightButtonClick = () => {
    dispatch({ type: `FIGHT_END` });
  };

  return (
    <div onClick={onEndFightButtonClick} className={mc.container}>
      FIGHTING SCENE
    </div>
  );
};

export default FightScene;
