import mc from "./pseudo-inputs.module.scss";
import { useDispatch, useSelector } from "react-redux";

const PseudoInputs = ({ inputValue, player, enterValidation, pseudoP1P2 }) => {
  const dispatch = useDispatch();

  const updatePseudo = (player, value) => {
    dispatch({ type: "PLAYER_PSEUDO", payload: { player, value } });
  };

  const { pseudoPlayer1Validate, pseudoPlayer2Validate } = useSelector(
    (store) => {
      return store.modificatorsReducer;
    }
  );

  return (
    <div className={mc.container}>
      <div className={mc.h3}>
        <h3>Chose your pseudo</h3>
        <h3>{pseudoP1P2}</h3>
      </div>

      <div className={mc.container_input}>
        <input
          className={mc.input}
          required={true}
          type="text"
          name="pseudo"
          autoComplete="off"
          value={inputValue}
          onChange={(e) => updatePseudo(player, e.target.value)}
          onKeyDown={enterValidation}
          //     // onAnimationEnd={() => handleAnimationEnd(setP1IsHidden)}
        />
        <label>
          {"Pseudo".split("").map((el, i) => (
            <span key={i} style={{ transitionDelay: `${i * 50}ms` }}>
              {el}
            </span>
          ))}
        </label>
      </div>
    </div>
  );
};

export default PseudoInputs;
