import mc from "./life-bar.module.scss";

const LifeBar = ({ player, playerPseudo, playerLife }) => {
  return (
    <div
      className={
        player === "player1"
          ? `${mc.container} ${mc.container_player1}`
          : player === "player2"
          ? `${mc.container} ${mc.container_player2}`
          : mc.container
      }
    >
      <div className={mc.player_class}>
        <span>classe</span>
      </div>
      <div className={mc.player_life_pseudo}>
        <div className={mc.pseudo}>
          <span>{playerPseudo}</span>
        </div>
        <div className={mc.life_bar}>
          <div
            className={mc.life_keep}
            style={{ "--remaining-life": `${playerLife}%` }}
          ></div>
        </div>
        <div className={mc.life}>
          <span>{`${playerLife} PV`}</span>
        </div>
      </div>
    </div>
  );
};

export default LifeBar;
