import mc from "./mdj-scene.module.scss";
//images//
import mdjImg from "../../../images/Mdj.png";
import { mdjWelcomeString1 } from "../../../constants/mdj";

const MdjScene = ({ player1, player2 }) => {
  return (
    <div className={mc.container}>
      <div className={`${mc.mdj_position} adaptive-img-contain`}>
        <span>
          <img src={mdjImg} alt="Image du maÃ®tre de jeu" draggable="false" />
        </span>
      </div>

      <div className={mc.mdj_dialogs}>
        <p>
          {mdjWelcomeString1.split(" ").map((el, i) => {
            if (el === "${player1}") {
              return (
                <span
                  key={i}
                  style={{
                    animationDelay: `${i * 0.1}s`,
                    color: `${player1.playerClass.color}`,
                  }}
                >
                  {player1.pseudo.toUpperCase()}
                </span>
              );
            }
          })}
        </p>
      </div>
    </div>
  );
};

export default MdjScene;
