import mc from "./players-menu.module.scss";
import { useSelector, useDispatch } from "react-redux";
//components//
import PseudoInputs from "../inputs/pseudoInputs/PseudoInputs";

const PlayersMenu = () => {
  const dispatch = useDispatch();

  const { player1, player2, pseudoPlayer1Validate, pseudoPlayer2Validate } =
    useSelector((store) => {
      return {
        player1: store.playersReducer.player1,
        player2: store.playersReducer.player2,
        pseudoPlayer1Validate: store.modificatorsReducer.pseudoPlayer1Validate,
        pseudoPlayer2Validate: store.modificatorsReducer.pseudoPlayer2Validate,
      };
    });

  const pseudoValidate = (e, playerNumber) => {
    console.log(e);

    // if (e.key === "Enter") {
    //   if (e.target.value.trim() !== "") {
    //     dispatch({ type: `PLAYER${playerNumber}_PSEUDO_VALIDATE` });
    //     // e.target.classList.remove("input_pseudo_empty");
    //     console.log(e);
    //   } else {
    //     dispatch({ type: `PLAYER${playerNumber}_PSEUDO_INPUT_ERROR` });
    //     // e.target.classList.add("input_pseudo_empty");
    //     console.log(e);
    //   }
    // }
  };

  const onEditPseudoClick = (playerNumber) => {
    dispatch({ type: `PLAYER${playerNumber}_PSEUDO_EDIT` });
  };

  return (
    <div className={mc.container}>
      <div className={mc.players_pseudo}>
        <div className={mc.player1}>
          {!pseudoPlayer1Validate ? (
            <PseudoInputs
              inputValue={player1.pseudo}
              player="player1"
              pseudoP1P2="Player1"
              enterValidation={(e) => pseudoValidate(e, 1)}
            />
          ) : (
            <div className={mc.pseudo_validate}>
              <span>{player1.pseudo}</span>
              <button onClick={() => onEditPseudoClick(1)}>
                <i className="icon-edit-2-outline"></i>
              </button>
            </div>
          )}
        </div>
        <div className={mc.player2}>
          <PseudoInputs
            inputValue={player2.pseudo}
            player="player2"
            pseudoP1P2="Player2"
          />
        </div>
      </div>
    </div>
  );
};

export default PlayersMenu;
