import mc from "./tutoriel.module.scss";
import { useEffect, useState } from "react";
import { tutorielWelcome } from "../../../constants/tutoriel";
import { tutoriel1 } from "../../../constants/tutoriel";
import { tutoriel2 } from "../../../constants/tutoriel";
import { tutoriel3 } from "../../../constants/tutoriel";

const Tutoriel = ({ player1, player2 }) => {
  const [animTitle, setAnimTitle] = useState(false);
  const [animTutoriel1, setAnimTutoriel1] = useState(false);
  const [animTutoriel2, setAnimTutoriel2] = useState(false);
  const [animTutoriel3, setAnimTutoriel3] = useState(false);

  useEffect(() => {
    setAnimTitle(true);
    setTimeout(() => {
      setAnimTutoriel1(true);
    }, 500);
    setTimeout(() => {
      setAnimTutoriel2(true);
    }, 1000);
  }, []);

  return (
    <div className={mc.container}>
      {animTitle && (
        <ul className={mc.title}>
          {tutorielWelcome.split(" ").map((el, i) => {
            if (el === "${player1}") {
              return (
                <h3 key={i}>
                  <span
                    style={{
                      animationDelay: `${i * 0.1}s`,
                      color: `${player1.playerClass.color}`,
                    }}
                  >
                    {player1.pseudo.toUpperCase()}
                  </span>
                </h3>
              );
            } else if (el === "${player2}") {
              return (
                <h3 key={i}>
                  <span
                    style={{
                      animationDelay: `${i * 0.1}s`,
                      color: `${player2.playerClass.color}`,
                    }}
                  >
                    {player2.pseudo.toUpperCase()}
                  </span>
                </h3>
              );
            } else {
              return (
                <h3 key={i}>
                  <span style={{ animationDelay: `${i * 0.1}s` }}>{el}</span>
                </h3>
              );
            }
          })}
        </ul>
      )}
      <div className={mc.tutoriel_content}>
        {animTutoriel1 && (
          <ul>
            {tutoriel1.split(" ").map((el, i) => {
              return (
                <h3 key={i}>
                  <span style={{ animationDelay: `${i * 0.1}s` }}>{el}</span>
                </h3>
              );
            })}
          </ul>
        )}
        {animTutoriel2 && (
          <ul>
            {tutoriel2.split(" ").map((el, i) => {
              return (
                <h3 key={i}>
                  <span style={{ animationDelay: `${i * 0.1}s` }}>{el}</span>
                </h3>
              );
            })}
          </ul>
        )}
        {animTutoriel3 && (
          <ul>
            {tutoriel3.split(" ").map((el, i) => {
              if (el === "${dés20}") {
                return (
                  <h3 key={i}>
                    <span
                      style={{
                        animationDelay: `${i * 0.1}s`,
                      }}
                      className={mc.des}
                    >
                      {"DÉS 20"}
                    </span>
                  </h3>
                );
              } else if (el === "${dés8}") {
                return (
                  <h3 key={i}>
                    <span
                      style={{
                        animationDelay: `${i * 0.1}s`,
                      }}
                      className={mc.des}
                    >
                      {"DÉS 8"}
                    </span>
                  </h3>
                );
              } else {
                return (
                  <h3 key={i}>
                    <span style={{ animationDelay: `${i * 0.1}s` }}>{el}</span>
                  </h3>
                );
              }
            })}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Tutoriel;
