import mc from "./pseudo-inputs.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";

const PseudoInputs = ({
  labelContent,
  inputValue,
  player,
  enterValidation,
  pseudoP1P2,
}) => {
  const dispatch = useDispatch();

  const updatePseudo = (player, value) => {
    dispatch({ type: "PLAYER_PSEUDO", payload: { player, value } });
  };

  return (
    <div className={mc.container}>
      <h3>{`Chose your pseudo ${pseudoP1P2}`}</h3>
      <div className={mc.container_input}>
        <input
          className={mc.input}
          required={true}
          type="text"
          name="pseudo"
          autoComplete="off"
          value={inputValue}
          onChange={(e) => updatePseudo(player, e.target.value)}
          onKeyDown={enterValidation}
          //     // onAnimationEnd={() => handleAnimationEnd(setP1IsHidden)}
        />
        <label>
          <span style={{ transitionDelay: "0ms" }}>P</span>
          <span style={{ transitionDelay: "50ms" }}>s</span>
          <span style={{ transitionDelay: "100ms" }}>e</span>
          <span style={{ transitionDelay: "150ms" }}>u</span>
          <span style={{ transitionDelay: "200ms" }}>d</span>
          <span style={{ transitionDelay: "250ms" }}>o</span>
        </label>
      </div>
    </div>
  );
};

export default PseudoInputs;
