import mc from "./logs-menu.module.scss";

const LogsMenu = ({ logs, player1, player2 }) => {
  return (
    <p className={mc.container}>
      {logs.split(" ").map((el, i) => (
        <li
          key={i}
          className={mc.log}
          style={{
            color:
              el === player1.pseudo
                ? player1.playerClass.color
                : el === player2.pseudo
                ? player2.playerClass.color
                : "white",
          }}
        >
          <span key={i} className={mc.log}>
            {el}
          </span>
        </li>
      ))}
    </p>
  );
};

export default LogsMenu;
