import mc from "./fight.module.scss";
//components//
import FightPlayerMenu from "../fight-player-menu/FightPlayerMenu";

const Fight = ({ player1, player2 }) => {
  return (
    <div className={mc.container}>
      <div className={mc.player1}></div>
      <div className={mc.logs}></div>
      <div className={mc.player2}>
        <div className={`${mc.player2_img} adaptive-img-contain`}>
          <span>
            <img
              src={
                player2.playerClass.className === "voleur"
                  ? rogueImgP2
                  : player2.playerClass.className === "mage"
                  ? mageImgP2
                  : warriorImgP2
              }
              alt={`{image du ${player2.playerClass.className}`}
            />
          </span>
        </div>
      </div>
    </div>
  );
};

export default Fight;
