const INITIAL_STATE = {
  pseudoPlayer1Validate: false,
  pseudoPlayer2Validate: false,
};

const modificatorsReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "PLAYER1_PSEUDO_VALIDATE": {
      return { ...state, pseudoPlayer1: true };
    }

    case "PLAYER2_PSEUDO_VALIDATE": {
      return {
        ...state,
        pseudoPlayer2: true,
      };
    }

    default: {
      return state;
    }
  }
};

export default modificatorsReducer;
