const INITIAL_USER = {
  id: 1,
  email: {
    value: "",
    selected: false,
  },
  emailConfirm: {
    value: "",
    selected: false,
  },
  password: {
    value: "",
    selected: false,
  },
  passwordConfirm: {
    value: "",
    selected: false,
  },
};

const userReducer = (state = INITIAL_USER, action) => {
  switch (action.type) {
    case "UPDATE_INPUT": {
      return {
        ...state,
        ...[action.payload.input],
        value: action.payload.value,
      };
    }

    case "LABEL_TOP": {
      return { ...state, ...[action.payload.input], selecred: true };
    }

    case "LABEL_DOWN": {
      return { ...state, ...[action.payload.input], selecred: false };
    }

    default: {
      return state;
    }
  }
};

export default userReducer;
