const INITIAL_STATE = {
  playersClickOnstartGame: false,
  startTv: false,
  round: 0,
  dice: {
    isOn: false,
    value: null,
  },
  logs: [],
};

const fightReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "LAUNCH_FIGHT": {
      return { ...state, playersClickOnstartGame: true };
    }
    case "START_TV": {
      return { ...state, startTv: true };
    }
    case "UPDATE_ROUND": {
      return { ...state, round: action.payload.round };
    }
    case "UPDATE_DICE": {
      return {
        ...state,
        dice: {
          ...state.dice,
          value: action.payload.value,
        },
      };
    }
    case "DICE_SHOW_HIDE": {
      return {
        ...state,
        dice: {
          ...state.dice,
          isOn: !state.isOn,
        },
      };
    }

    case "UPDATE_LOGS": {
      return {
        ...state,
        dice: {
          ...state.dice,
          isOn: !state.isOn,
        },
      };
    }

    default: {
      return state;
    }
  }
};

export default fightReducer;
