import mc from "./fight.module.scss";
//components//
import FightPlayerMenu from "../fight-player-menu/FightPlayerMenu";
import LogsMenu from "../logsMenu/LogsMenu";

const Fight = ({
  windowWidth,
  player1,
  player2,
  logs,
  onAttackClickP1,
  onDefenseClick,
  onUltimClick,
  onPotionClick,
}) => {
  return (
    <div className={mc.container}>
      <div className={mc.player1}>
        <FightPlayerMenu
          playerNumber={1}
          player={player1}
          windowWidth={windowWidth}
          onAttackClick={onAttackClickP1}
          onDefenseClick={onDefenseClick}
          onUltimClick={onUltimClick}
          onPotionClick={onPotionClick}
        />
      </div>
      <div className={mc.logs}>
        <LogsMenu logs={logs} />
      </div>
      <div className={mc.player2}>
        <FightPlayerMenu
          windowWidth={windowWidth}
          playerNumber={2}
          player={player2}
          // onAttackClick={onAttackClick}
          onDefenseClick={onDefenseClick}
          onUltimClick={onUltimClick}
          onPotionClick={onPotionClick}
        />
      </div>
    </div>
  );
};

export default Fight;
