import { useEffect } from "react";
import mc from "./logs-menu.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { useRef } from "react";

const LogsMenu = () => {
  const dispatch = useDispatch();
  //   logsReducer

  const { logs } = useSelector((store) => {
    return {
      logs: store.logsReducer.logs,
    };
  });

  //   console.log(logs);
  const lastLogRef = useRef();

  useEffect(() => {
    dispatch({
      type: "UPDATE_LOGS",
      payload: { newlog: "test4" },
    });

    if (lastLogRef.current) {
      lastLogRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, []);

  useEffect(() => {
    dispatch({
      type: "RESET_LOGS",
      payload: { newlog: <li>test</li> },
    });
  }, []);

  return (
    <ul className={mc.container}>
      {logs.map((el, i) => (
        <li
          key={i}
          className={`${mc.log} ${
            el === logs[logs.length - 1] ? mc.animate : ""
          }`}
          ref={el === logs[logs.length - 1] ? lastLogRef : null}
        >
          <span>{el}</span>
        </li>
      ))}
    </ul>
  );
};

export default LogsMenu;
