import mc from "./sign-up.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { setItem } from "../../../utils/storage.utils";
import InputLogin from "../inputs/inputLogin";

const SignUp = ({ onError }) => {
  const dispatch = useDispatch();
  const { signUp } = useSelector((store) => {
    return { signUp: store.signUpReducer };
  });

  const handleInputChange = (key, value) => {
    dispatch({ type: "UPDATE_SIGNUP_FIELD", payload: { key, value } });
  };

  const labelUp = (e, input) => {
    const action = {
      type: "LABEL_TOP",
      payload: {
        input: input,
      },
    };
    dispatch(action);
  };

  const closeLogin = () => {
    const action = {
      type: "CLOSE_LOGIN",
    };
    dispatch(action);
  };

  const openSignIn = () => {
    const action = {
      type: "OPEN_LOGIN",
    };
    dispatch(action);
    handleInputChange("password", "");
    handleInputChange("password", "");
    handleInputChange("password", "");
    handleInputChange("password", "");
  };

  const cleanInputsValue = () => {
    const action = {
      type: "CLEAN_SIGNUP_INPUTS",
    };
    dispatch(action);
  };

  const isLogged = () => {
    const action = {
      type: "IS_LOGGED",
    };
    dispatch(action);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    cleanInputsValue();
    // let result;
    // let status;

    // const form = {
    //   pseudo: signUp.pseudo.value,
    //   email: signUp.email.value,
    //   confirmEmail: signUp.confirmEmail.value,
    //   password: signUp.password.value,
    //   confirmPassword: signUp.confirmPassword.value,
    // };

    // const config = {
    //   method: "POST",
    //   body: JSON.stringify(form),
    //   headers: {
    //     "content-type": "application/json; charset=UTF-8",
    //   },
    // };

    // try {
    //   const response = await fetch(
    //     `http://localhost:7001/users/sign-up`,
    //     config
    //   );
    //   status = response.status;
    //   result = await response.json();
    // } catch (e) {
    //   console.error(`Problem with request ${e.message}`);
    //   onError();
    //   return;
    // }

    // if (status >= 400) {
    //   onError(status);
    //   return;
    // }

    // const token = result.token;
    // // window.localStorage.setItem("token", JSON.stringify(token));
    // setItem("token", token);
    // isLogged();
  };

  return (
    <div className={mc.container}>
      <div className={mc.login_close}>
        <button onClick={closeLogin}>X</button>
      </div>

      <form className={mc.form} onSubmit={(e) => handleSubmit(e)}>
        <div className={mc.title}>
          <p>Inscription</p>
        </div>
        <div className={mc.inputs}>
          <InputLogin
            // labelContent="Pseudo"
            inputType="text"
            inputValue={signUp.pseudo.value}
            handleInputChange={(e) =>
              handleInputChange("pseudo", e.target.value)
            }
            labelUp={(e) => labelUp(e, "pseudo")}
          />
          <div className={mc.email}>
            <InputLogin
              // labelContent="Email"
              inputType="email"
              inputValue={signUp.email.value}
              handleInputChange={(e) =>
                handleInputChange("email", e.target.value)
              }
              labelUp={(e) => labelUp(e, "email")}
            />
            <InputLogin
              // labelContent="Confirm email"
              inputType="email"
              inputValue={signUp.confirmEmail.value}
              handleInputChange={(e) =>
                handleInputChange("confirmEmail", e.target.value)
              }
              labelUp={(e) => labelUp(e, "confirmEmail")}
            />
          </div>
          <div className={mc.password}>
            <InputLogin
              // labelContent="Password"
              inputType="password"
              inputValue={signUp.password.value}
              handleInputChange={(e) =>
                handleInputChange("password", e.target.value)
              }
              labelUp={(e) => labelUp(e, "password")}
            />
            <InputLogin
              // labelContent="Confirm password"
              inputType="password"
              inputValue={signUp.confirmPassword.value}
              handleInputChange={(e) =>
                handleInputChange("confirmPassword", e.target.value)
              }
              labelUp={(e) => labelUp(e, "confirmPassword")}
            />
          </div>
        </div>
        <div className={mc.connect}>
          <button type="submit" onClick={(e) => handleSubmit(e)}>
            S'incrire
          </button>
          <span onClick={openSignIn}>Déjà client ?</span>
        </div>
      </form>
    </div>
  );
};

export default SignUp;
