import mc from "./login.module.scss";
import SignIn from "./sign-in/SignIn";
import { useSelector } from "react-redux";
import SignUp from "./sign-up/SignUp";

const Login = () => {
  const HOME_STATE = {
    signIn: "sign-in",
    signUp: "sign-up",
  };

  const { modificator } = useSelector((store) => {
    return { modificator: store.modificatorsReducer };
  });

  const [error, setError] = useState("");

  const onError = (status) => {
    switch (status) {
      case 400:
        setError("Les données sont incorrectes");
        break;
      case 401:
        setError("Mauvaise authentification");
        break;
      case 403:
        setError("L'utilisateur existe deja");
        break;
      default:
        setError("Erreur du serveur");
        break;
    }
  };

  return (
    <div
      className={
        modificator.loginView ? mc.container : `${mc.container} ${mc.hide}`
      }
    >
      {modificator.switchSignInSignUp ? <SignUp /> : <SignIn />}
    </div>
  );
};

export default Login;
