import mc from "./pseudo-inputs.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";

const PseudoInputs = ({
  labelContent,
  inputValue,
  player,
  enterValidation,
}) => {
  const dispatch = useDispatch();
  const { player1, player2 } = useSelector((store) => {
    return store.playersReducer;
  });
  const updatePseudo = (player, value) => {
    dispatch({ type: "PLAYER_PSEUDO", payload: { player, value } });
    console.log({ player1, player2 });
  };

  //Animation input test//

  const [isHidden, setIsHidden] = useState(false);

  const handleAnimationEnd = () => {
    setIsHidden(true);
  };

  return (
    <div className={!isHidden ? mc.container : mc.container_hidden}>
      <label className={mc.label}>{labelContent}</label>
      <input
        className={isHidden ? mc.hidden : mc.input}
        required={true}
        type="text"
        name="pseudo"
        autoComplete="off"
        value={inputValue}
        onChange={(e) => updatePseudo(player, e.target.value)}
        onKeyDown={enterValidation}
        onAnimationEnd={handleAnimationEnd}
      ></input>
      <div className={mc.underline}></div>
    </div>
  );
};

export default PseudoInputs;
