import mc from "./main.module.scss";
import { useSelector } from "react-redux";
//components//
import PlayersMenu from "../players-menu/PlayersMenu";
import FightScene from "../fight-scene/FightScene";

const Main = () => {
  const { fightStart } = useSelector((store) => {
    return store.modificatorsReducer;
  });

  return (
    <div className={mc.container}>
      {fightStart ? (
        <div>
          <FightScene />
        </div>
      ) : (
        <div
          className={
            fightStart
              ? `${mc.fight_scene} ${mc.animate_slide_right}`
              : `${mc.fight_scene} ${mc.animate_right_left_fade_in}`
          }
        >
          <PlayersMenu />
        </div>
      )}
    </div>
  );
};

export default Main;
