import mc from "./players-menu.module.scss";
import { useSelector, useDispatch } from "react-redux";
//components//
import PseudoInputs from "../menus/pseudoInputs/PseudoInputs";
import ClassSelection from "../menus/class-selection/ClassSelection";

const PlayersMenu = () => {
  const dispatch = useDispatch();

  const {
    player1,
    player2,
    pseudoPlayer1Validate,
    pseudoPlayer2Validate,
    inputP1ValidationError,
    inputP2ValidationError,
  } = useSelector((store) => {
    return {
      player1: store.playersReducer.player1,
      player2: store.playersReducer.player2,
      pseudoPlayer1Validate: store.modificatorsReducer.pseudoPlayer1Validate,
      pseudoPlayer2Validate: store.modificatorsReducer.pseudoPlayer2Validate,
      inputP1ValidationError: store.modificatorsReducer.inputP1ValidationError,
      inputP2ValidationError: store.modificatorsReducer.inputP2ValidationError,
    };
  });

  const pseudoValidate = (e, playerNumber) => {
    if (e.key === "Enter") {
      if (e.target.value.trim() !== "") {
        dispatch({ type: `PLAYER${playerNumber}_PSEUDO_VALIDATE` });
        dispatch({ type: `PLAYER${playerNumber}_PSEUDO_INPUT_ERROR_RESET` });
      } else {
        dispatch({ type: `PLAYER${playerNumber}_PSEUDO_INPUT_ERROR` });
        setTimeout(() => {
          dispatch({ type: `PLAYER${playerNumber}_PSEUDO_INPUT_ERROR_RESET` });
        }, 1000);
      }
    }
  };

  const onEditPseudoClick = (playerNumber) => {
    dispatch({ type: `PLAYER${playerNumber}_PSEUDO_EDIT` });
    dispatch({
      type: "PLAYER_PSEUDO",
      payload: { player: `player${playerNumber}`, value: "" },
    });
  };

  const playerClassSelection = () => {};

  return (
    <div className={mc.container}>
      <div className={mc.players_pseudo}>
        <div className={mc.player1}>
          {!pseudoPlayer1Validate ? (
            <PseudoInputs
              inputValue={player1.pseudo}
              player="player1"
              pseudoInTitle="Player1"
              enterValidation={(e) => pseudoValidate(e, 1)}
              inputValidationError={inputP1ValidationError}
            />
          ) : (
            <ClassSelection
              pseudo={player1.pseudo}
              onClick={() => onEditPseudoClick(1)}
              playerClassSelection={playerClassSelection}
            />
          )}
        </div>
        <div className={mc.player2}>
          {!pseudoPlayer2Validate ? (
            <PseudoInputs
              inputValue={player2.pseudo}
              player="player2"
              pseudoInTitle="Player2"
              enterValidation={(e) => pseudoValidate(e, 2)}
              inputValidationError={inputP2ValidationError}
            />
          ) : (
            <ClassSelection
              pseudo={player2.pseudo}
              onClick={() => onEditPseudoClick(2)}
              playerClassSelection={playerClassSelection}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default PlayersMenu;
