import mc from "./tv.module.scss";

const Tv = ({ round, player1, player2 }) => {
  return (
    <div className={mc.container}>
      {/* <div className={mc.dice_loading}>
        <span>Le maître du jeu lance le dé...</span>
      </div> */}
      <div className={mc.rounds}>
        <span>
          {round !== 0 && round % 2 === 1 ? (
            <>
              À <span className={mc.playerName}>{player1.pseudo}</span> de jouer
            </>
          ) : round !== 0 && round % 2 === 0 ? (
            <>
              À <span className={mc.playerName}>{player2.pseudo}</span> de jouer
            </>
          ) : (
            ""
          )}
        </span>
      </div>
    </div>
  );
};

export default Tv;
