const INITIAL_STATE = {
  words: [],
  counter: 0,
};

const wordsReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "WORD_ADD": {
      const newCounter = state.counter + 1;
      const word = {
        // value: generateWord(4, 10),
        id: newCounter,
        selected: false,
      };
      const newState = {
        ...state,
        words: state.words.length <= 5 ? [...state.words, word] : [word],
        counter: newCounter,
      };
      return newState;
    }

    case "ADD_CLASS": {
      return {
        ...state,
        words: state.words.map((el) =>
          el.id === action.payload.id
            ? { ...el, selected: !el.selected }
            : { ...el }
        ),
      };
    }

    default: {
      return state;
    }
  }
};

export default wordsReducer;
