import mc from "./fight.module.scss";
//images//
import rogueImg from "../../../images/rogueImg.png";
import mageImg from "../../../images/mageImg.png";
import warriorImg from "../../../images/warriorImg.png";

const Fight = ({ player1, player2 }) => {
  return (
    <div className={mc.container}>
      <div className={mc.player1}>
        <img
          src={
            player1.playerClass.className === "voleur"
              ? rogueImg
              : player1.playerClass.className === "mage"
              ? mageImg
              : warriorImg
          }
          alt={`{image ${player1.playerClass.className}`}
        />
      </div>
      <div className={mc.logs}></div>
      <div className={mc.player2}>
        <img
          src={
            player2.playerClass.className === "voleur"
              ? rogueImg
              : player2.playerClass.className === "mage"
              ? mageImg
              : warriorImg
          }
          alt={`{image du ${player2.playerClass.className}`}
        />
      </div>
    </div>
  );
};

export default Fight;
