import mc from "./pseudo-inputs.module.scss";
import { useDispatch, useSelector } from "react-redux";

const PseudoInputs = ({
  inputValue,
  player,
  enterValidation,
  pseudoInTitle,
  inputValidationError,
}) => {
  const dispatch = useDispatch();

  const updatePseudo = (player, value) => {
    dispatch({ type: "PLAYER_PSEUDO", payload: { player, value } });
  };

  const { inputP1ValidationError, inputP2ValidationError } = useSelector(
    (store) => {
      return {
        inputP1ValidationError:
          store.modificatorsReducer.inputP1ValidationError,
        inputP2ValidationError:
          store.modificatorsReducer.inputP2ValidationError,
      };
    }
  );

  return (
    <div className={mc.container}>
      <div className={mc.h3}>
        <h3>CHOISIS TON PSEUDO</h3>
        <span className={mc.player1_2}>{pseudoInTitle}</span>
      </div>

      <div className={mc.container_input}>
        <input
          className={
            !inputValidationError ? mc.input : `${mc.input} input_empty`
          }
          required={true}
          type="text"
          name="pseudo"
          autoComplete="off"
          value={inputValue}
          onChange={(e) => updatePseudo(player, e.target.value)}
          onKeyDown={enterValidation}
          //     // onAnimationEnd={() => handleAnimationEnd(setP1IsHidden)}
        />
        <label>
          {"Ton  pseudo".split("").map((el, i) => (
            <span key={i} style={{ transitionDelay: `${i * 30}ms` }}>
              {el}
            </span>
          ))}
        </label>
      </div>
    </div>
  );
};

export default PseudoInputs;
