import { useState } from "react";
import mc from "./sign-in.module.scss";
import { useDispatch, useSelector } from "react-redux";

const SignIn = () => {
  const dispatch = useDispatch();
  const { user } = useSelector((store) => {
    return { user: store.userReducer };
  });

  const handleInputChange = (e, input) => {
    const action = {
      type: "UPDATE_INPUT",
      payload: {
        value: e.target.value,
        input: input,
      },
    };
    dispatch(action);
  };

  const handleInputEnter = (e, input) => {
    const action = {
      type: "LABEL_TOP",
      payload: {
        input: input,
      },
    };
    dispatch(action);
  };

  const handleInputLeave = (e, input) => {
    const action = {
      type: "LABEL_DOWN",
      payload: {
        input: input,
      },
    };
    dispatch(action);
  };

  const handleXClick = () => {
    const action = {
      type: "CLOSE_LOGIN",
    };
    dispatch(action);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className={mc.container}>
      <div className={mc.login_close}>
        <button onClick={handleXClick}>X</button>
      </div>
      <div>
        <p className={mc.title}>Connection</p>
        <form onSubmit={(e) => handleSubmit(e)}>
          <div className={mc.inputs}>
            <div className={mc.inputs_left}>
              <div className={mc.inputs_blocks}>
                <label className={user.email.selected ? mc.label_up : {}}>
                  Email
                </label>
                <input
                  required={true}
                  type="email"
                  value={user.email.value}
                  onChange={(e) => handleInputChange(e, "email")}
                  onMouseEnter={(e) => handleInputEnter(e)}
                  onMouseLeave={(e) => handleInputLeave(e)}
                ></input>
              </div>
              <div className={mc.inputs_blocks}>
                <label className={user.password.selected ? mc.label_up : {}}>
                  Password
                </label>
                <input
                  required={true}
                  type="password"
                  value={user.password.value}
                  onChange={(e) => handleInputChange(e, "password")}
                  onMouseEnter={(e) => handleInputEnter(e)}
                  onMouseLeave={(e) => handleInputLeave(e)}
                ></input>
              </div>
            </div>

            <div className={mc.right}>
              <div className={mc.inputs_blocks}>
                <label
                  className={user.emailConfirm.selected ? mc.label_up : {}}
                >
                  Confirm Email
                </label>
                <input
                  required={true}
                  type="email"
                  value={user.emailConfirm.value}
                  onChange={(e) => handleInputChange(e, "emailConfirm")}
                  onMouseEnter={(e) => handleInputEnter(e)}
                  onMouseLeave={(e) => handleInputLeave(e)}
                ></input>
              </div>
              <div className={mc.inputs_blocks}>
                <label
                  className={user.passwordConfirm.selected ? mc.label_up : {}}
                >
                  Confirm Password
                </label>
                <input
                  required={true}
                  type="password"
                  value={user.passwordConfirm.value}
                  onChange={(e) => handleInputChange(e, "passwordConfirm")}
                  onMouseEnter={(e) => handleInputEnter(e)}
                  onMouseLeave={(e) => handleInputLeave(e)}
                ></input>
              </div>
            </div>
          </div>
          <div className={mc.connect}>
            <button type="submit" onClick={(e) => handleSubmit(e)}>
              Se connecter
            </button>
            <span>S'incrire</span>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SignIn;
