import { useState } from "react";
import mc from "./sign-in.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { setItem } from "../../../utils/storage.utils";
import InputLogin from "../inputs/inputLogin";

const SignIn = ({ onError }) => {
  const dispatch = useDispatch();
  const { signIn } = useSelector((store) => {
    return { singIn: store.signInReducer };
  });

  const isLogged = () => {
    const action = {
      type: "IS_LOGGED",
    };
    dispatch(action);
  };

  const handleInputChange = (key, value) => {
    dispatch({ type: "UPDATE_SIGNIN_FIELD", payload: { key, value } });
  };

  const cleanInputValue = (input) => {
    const action = {
      type: "CLEAN_INPUT",
      payload: {
        input: input,
      },
    };
    dispatch(action);
  };

  const labelUp = (e, input) => {
    const action = {
      type: "LABEL_TOP",
      payload: {
        input: input,
      },
    };
    dispatch(action);
  };

  const closeLogin = () => {
    const action = {
      type: "CLOSE_LOGIN",
    };
    dispatch(action);
  };

  const openSignUp = () => {
    const action = {
      type: "switchSignUp",
    };
    dispatch(action);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let result;
    let status;

    const form = {
      email: signIn.email.value,
      confirmEmail: signIn.emailConfirm.value,
      password: signIn.password.value,
      confirmPassword: signIn.passwordConfirm.value,
    };

    const config = {
      method: "POST",
      body: JSON.stringify(form),
      headers: {
        "content-type": "application/json; charset=UTF-8",
      },
    };

    try {
      const response = await fetch(
        `http://localhost:7001/users/sign-in`,
        config
      );
      status = response.status;
      result = await response.json();
    } catch (e) {
      console.error(`Problem with request ${e.message}`);
      onError();
      return;
    }

    if (status >= 400) {
      onError(status);
      return;
    }

    cleanInputValue("email");
    cleanInputValue("password");

    const token = result.token;
    // window.localStorage.setItem("token", JSON.stringify(token));
    setItem("token", token);
    isLogged();
  };

  return (
    <div className={mc.container}>
      <div className={mc.login_close}>
        <button onClick={closeLogin}>X</button>
      </div>
      <form className={mc.form} onSubmit={(e) => handleSubmit(e)}>
        <div>
          <p className={mc.title}>Connection</p>
        </div>

        <div className={mc.inputs}>
          <InputLogin
            labelContent="Email"
            inputType="email"
            inputValue={signIn.email.value}
            handleInputChange={(e) =>
              handleInputChange(e.target.value, "email")
            }
            labelUp={(e) => labelUp(e, "email")}
          />
          <InputLogin
            labelContent="Password"
            inputType="password"
            inputValue={signIn.password.value}
            handleInputChange={(e) => handleInputChange(e, "password")}
            labelUp={(e) => labelUp(e, "password")}
          />
        </div>

        <div className={mc.connect}>
          <button type="submit" onClick={(e) => handleSubmit(e)}>
            Se connecter
          </button>
          <span onClick={openSignUp}>S'incrire</span>
        </div>
      </form>
    </div>
  );
};

export default SignIn;
