export const mdjWelcomeString = [
  "Bienvenue dans l'arène ${player1} et ${player2} ! Je m'apelle Merlin SIMPA. Comme mon nom vous l'indique, je serai votre sympathique guide lors de ce combat à mort ! Cliquez au dessus pour commencer.",
  "Bienvenue dans l'arène ${player1} et ${player2} ! Je suis Merlin SIMPA. Je serai votre maître du jeu aujourd'hui. Comptez sur moi pour vous prodiguer conseils et encouragements ! Cliquez au dessus pour commencer.",
];

export const mdjFightWillStart = "Que le combat commence !";

export const mdjTauntAttacker = [
  "${attacker} rate sa cible, il devrait peut-être passer chez l'ophtalmo !",
  "${attacker} manque sa cible, il manquerait une vache dans un tout petit couloir avec un énorme bazooka !",
  "${attacker} rate sa cible, ${defender} est aussi agile qu'un chat ! Ou ${attacker} est vraiment mauvais, je vous laisse en juger.",
  "${attacker} rate sa cible, ${attacker} devrait peut-être se repprocher un peu de sa cible, on aurait une chance de voir un vrai combat...",
];

export const mdjTauntDefender = [
  "Outch ! ${defender} encaisse ce coup. ${defender} est lent... Oui ${defender} tu es lent ! Cette appli est superbement bien optimisée, il n'y a aucun lags ici !!",
  "L'attaque de ${attacker} était vraiment téléphonée, comment ${defender} peut ne pas esquiver ça...",
  "${defender} prend ce coup de plein fouet ! Hey ${defender} on se réveille, même ma grand mère l'aurait esquivée celle là !!!",
  "${defender} pare magnifiquement cette attaque ! ... avec sa tête ...",
];

export const mdjPotionOk = [
  "Hey ! Une potion?! C'est de la triche ça !!",
  "Je crois bien que ${attacker} se dope...",
  "Voilà que ${attacker} se soigne... Comme si ${defender} était pas assez mauvais !",
];

export const mdjPotionNok = [
  "Tu essayes de soigner quoi là? Ta bêtise? C'est pas le bon remède.",
  "Je comprends maintenant pourquoi ${attacker} est un si mauvais combattant... Il est aveugle. TES POINTS DE VIE SONT COMPLETS !!!",
  "300PV/300PV... hey ${attacker} faut arrêter d'enfoncer le coton tige quand tu sents que ça résiste !",
];

export const mdjNoMorePotion = [
  "C'est vide ! Vide ! Zéro ! NADA ! NICHTS ! NOTHING MOOOOOORE !!!",
  "plus de potion, c'est marqué dessus... tu me fatigues ${attacker} ...",
  "ZÉRO !! Y A MARQUÉ ZÉRO POTION !! Hey ${attacker} je comprends pourquoi t'en touches pas une !",
];
