import mc from "./cinema.module.scss";
import { useState, useEffect } from "react";

const Cinema = () => {
  const [films, setFilms] = useState([]);

  const getAllFilms = async () => {
    const json = await fetch("http://localhost:7001/cinema/get-all");
    const response = await json.json();
    setFilms(response.data);
  };

  useEffect(() => {
    getAllFilms();
  }, []);

  return (
    <div className={mc.container}>
      <ul className={mc.film}>
        {films.map((el, i) => (
          <li key={i}>
            <div className={mc.left}>
              <span>
                <img src={`http://localhost:7001/${el.image}`} />
              </span>
            </div>
            <div className={mc.right}>
              <div className={mc.title}>
                <h3>{el.title}</h3>
              </div>

              <div className={mc.description}>
                <div className={mc.release_date}>
                  <h4>Date de sortie:</h4>
                  <p>{el.release}</p>
                </div>

                <div className={mc.kind}>
                  <h4>Genre:</h4>
                  <ul>
                    {el.type.map((el, j) => (
                      <li key={j}>
                        <p>{el}</p>
                      </li>
                    ))}
                  </ul>
                </div>

                <div className={mc.director}>
                  <h4>De:</h4>
                  <ul>
                    {el.director.map((el, j) => (
                      <li key={j}>
                        <p>{el}</p>
                      </li>
                    ))}
                  </ul>
                </div>

                <div className={mc.actors}>
                  <h4>Avec:</h4>
                  <ul>
                    {el.actors.map((el, j) => (
                      <li key={j}>
                        <p>{el}</p>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>

              <div className={mc.synopsis}>
                <h4>Synopsis</h4>
                <p>{el.synopsis}</p>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Cinema;
