import mc from "./life-bar.module.scss";
//utils//
import { firstLetterUpper } from "../../../utils/firstLetterCapital";

const LifeBar = ({ playerNumber, player }) => {
  return (
    <div
      className={
        playerNumber === 1
          ? `${mc.container} ${mc.container_player1}`
          : playerNumber === 2
          ? `${mc.container} ${mc.container_player2}`
          : mc.container
      }
    >
      <div
        className={
          playerNumber === 1
            ? `${mc.player_class} ${mc.player_class_player1}`
            : playerNumber === 2
            ? `${mc.player_class} ${mc.player_class_player2}`
            : mc.container
        }
      >
        <span>{firstLetterUpper(player.playerClass.className)}</span>
      </div>

      <div className={mc.player_life_pseudo}>
        <div className={mc.pseudo}>
          <span>{player.pseudo}</span>
        </div>
        <div className={mc.life_bar}>
          <div className={mc.life}>
            <span>{`${player.playerClass.hp} PV`}</span>
          </div>
          <div
            className={mc.life_remaining}
            style={{ "--remaining-life": `${player.playerClass.hp}%` }}
          ></div>
        </div>
      </div>
      {/* test */}
    </div>
  );
};

export default LifeBar;
