const INITIAL_STATE = {
  player1: {
    pseudo: "Zokk",
    playerClass: {}, //à décommenter
    // playerClass: {
    //   className: "voleur",
    //   hp: 100,
    //   atk: 44,
    //   int: 20,
    //   spd: 80,
    //   agi: 60,
    //   color: "#FFF468",
    //   icon: "rogueIcon",
    // },
    // DEBUG todelete objet vide
  },
  player2: {
    pseudo: "Salix",
    playerClass: {}, //à décommenter
    // playerClass: {
    //   className: "mage",
    //   hp: 100,
    //   atk: 16,
    //   int: 150,
    //   spd: 50,
    //   agi: 50,
    //   color: "#3FC7EB",
    //   icon: "mageIcon",
    // }, // DEBUG todelete objet vide
  },
};

const playersReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "PLAYER_PSEUDO": {
      return {
        ...state,
        [action.payload.player]: {
          ...state[action.payload.player],
          pseudo: action.payload.pseudo,
        },
      };
    }

    case "PLAYER_CLASS": {
      return {
        ...state,
        [action.payload.player]: {
          ...state[action.payload.player],
          playerClass: action.payload.playerClass,
        },
      };
    }

    default: {
      return state;
    }
  }
};

export default playersReducer;
