  //         height={100}
  //         fill={color}
  //         shadowBlur={5}
  //         onClick={handleClick}
  //       />
  //     );
  //   };

  return (
    <div
      // onClick={onEndFightButtonClick}
      className={mc.container}
    >
      <div className={mc.canvas_game} ref={canvasGameRef}>
        <Stage>
          <Layer>
            <Rect width={800} height={stageHeight} fill="black" />
            <Text text="Try click on rect" />
            {/* <ColoredRect /> */}
          </Layer>
        </Stage>
      </div>
      <span onClick={onEndFightButtonClick}>reset</span>
    </div>
  );
};

export default FightScene;
