import mc from "./fight-scene.module.scss";
import { useDispatch } from "react-redux";
import { Stage, Layer, Rect, Text } from "react-konva";
import Konva from "konva";
import { useState, useEffect, useRef } from "react";

const FightScene = () => {
  const dispatch = useDispatch();
  const canvasGameRef = useRef(null);
  const [stageWidth, setStageWidth] = useState(100);
  const [stageHeight, setStageHeight] = useState(100); // Initialize with a default value

  const onEndFightButtonClick = () => {
    dispatch({ type: `ANIMATION_FIGHT_START_CANCEL` });
    setTimeout(() => {
      dispatch({ type: `FIGHT_END` });
    }, 300);
  };

  const updateStageDimensions = () => {
    if (canvasGameRef.current) {
      const width = canvasGameRef.current.clientWidth;
      const height = canvasGameRef.current.clientHeight;
      setStageWidth(width);
      setStageHeight(height);
    }
  };

  useEffect(() => {
    updateStageDimensions();
    window.addEventListener("resize", updateStageDimensions);
    return () => {
      window.removeEventListener("resize", updateStageDimensions);
    };
  }, []);

  const ColoredRect = () => {
    const [color, setColor] = useState("green");
    const handleClick = () => {
      setColor(Konva.Util.getRandomColor());
    };
    const rectX = (stageWidth - 100) / 2;
    return (
      <Rect
        x={rectX}
        y={0}
        width={100}
        height={100}
        fill={color}
        shadowBlur={5}
        onClick={handleClick}
      />
    );
  };

  return (
    <div
      // onClick={onEndFightButtonClick}
      className={mc.container}
    >
      <div className={mc.canvas_game} ref={canvasGameRef}>
        <Stage width={stageWidth} height={stageHeight}>
          <Layer>
            <Rect width={stageWidth} height={stageHeight} fill="black" />
            <Text text="Try click on rect" />
            <ColoredRect />
          </Layer>
        </Stage>
      </div>
    </div>
  );
};

export default FightScene;
