const INITIAL_STATE = {
  atk: 44,
  int: 20,
  spd: 80,
  agi: 60,
  color: "#FFF468",
};

const rogueCharacReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "OPEN_SIGNIN": {
      return { ...state, loginView: true, switchSignInSignUp: false };
    }

    case "CLOSE_LOGIN": {
      return { ...state, loginView: false };
    }

    case "OPEN_SIGNUP": {
      return { ...state, switchSignInSignUp: true };
    }

    default: {
      return state;
    }
  }
};

export default rogueCharacReducer;
