import { useEffect } from "react";
import mc from "./logs-menu.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { useRef } from "react";

const LogsMenu = () => {
  const dispatch = useDispatch();
  //   logsReducer

  const { logs } = useSelector((store) => {
    return {
      logs: store.logsReducer.logs,
    };
  });

  console.log(logs);

  useEffect(() => {
    dispatch({
      type: "UPDATE_LOGS",
      payload: { newlog: "test15" },
    });
  }, []);

  const logsListRef = useRef(null);

  useEffect(() => {
    if (logsListRef.current) {
      // Scroll to the bottom when a new log is added
      logsListRef.current.scrollTop = logsListRef.current.scrollHeight;
    }
  }, [logs]);

  //   useEffect(() => {
  //     dispatch({
  //       type: "RESET_LOGS",
  //       payload: { newlog: <li>test</li> },
  //     });
  //   }, []);

  return (
    <ul className={mc.container} ref={logsListRef}>
      {logs.map((el, i) => (
        <li key={i} className={`${mc.log} ${mc.appear}`}>
          <span>{el}</span>
        </li>
      ))}
    </ul>
  );
};

export default LogsMenu;
