const INITIAL_USER = {
  id: 1,
  email: {
    value: "",
    selected: false,
  },
  emailConfirm: {
    value: "",
    selected: false,
  },
  password: {
    value: "",
    selected: false,
  },
  passwordConfirm: {
    value: "",
    selected: false,
  },
  error: "",
};

const userReducer = (state = INITIAL_USER, action) => {
  switch (action.type) {
    case "UPDATE_INPUT": {
      return {
        ...state,
        [action.payload.input]: {
          ...state[action.payload.input],
          value: action.payload.value,
        },
      };
    }

    case "CLEAN_INPUTS_ERROR": {
      return {
        ...state,
        [action.payload.input]: {
          value: "",
        },
        // ...state,
        // error: "",
      };
    }

    case "LABEL_TOP": {
      return {
        ...state,
        [action.payload.input]: {
          ...state[action.payload.input],
          selected: true,
        },
      };
    }

    case "LABEL_DOWN": {
      return {
        ...state,
        [action.payload.input]: {
          ...state[action.payload.input],
          selected: false,
        },
      };
    }

    default: {
      return state;
    }
  }
};

export default userReducer;
