import mc from "./pseudo-inputs.module.scss";
import { Dispatch } from "react";
import { useSelector, useDispatch } from "react-redux";

const PseudoInputs = ({
  labelContent,
  inputValue,
  updateField,
  handleInputActive,
  handleInputInactive,
  labelUp,
}) => {
  return (
    <div className={mc.container}>
      <label className={labelUp ? `${mc.label} ${mc.labelUp}` : mc.label}>
        {labelContent}
      </label>
      <input
        className={mc.input}
        required={true}
        type="text"
        name="pseudo"
        autoComplete="off"
        value={inputValue}
        onChange={updateField}
        onFocus={handleInputActive}
        onBlur={handleInputInactive}
      ></input>
    </div>
  );
};

export default PseudoInputs;
