import mc from "./players-menu.module.scss";
import { useSelector } from "react-redux";
//components//
import PseudoInputs from "../inputs/pseudoInputs/PseudoInputs";

const PlayersMenu = () => {
  const { player1, player2 } = useSelector((store) => {
    return store.playersReducer;
    console.log(player1);
  });

  const updatePseudo = (key, value) => {
    dispatch({ type: "PLAYER_PSEUDO", payload: { key, value } });
  };

  return (
    <div className={mc.container}>
      <div className={mc.players}>
        <div className={mc.player1}>
          <h3>Player1</h3>
          <PseudoInputs
            //   labelContent,
            inputValue={player1.pseudo}
            updatePseudo={(e) => updatePseudo(pseudo, e.target.value)}
          />
        </div>
        <div className={mc.player2}>
          <h3>Player2</h3>
          <PseudoInputs />
        </div>
      </div>
    </div>
  );
};

export default PlayersMenu;
