import mc from "./fight-scene.module.scss";
import { useSelector, useDispatch } from "react-redux";

const FightScene = () => {
  const dispatch = useDispatch();

  const onEndFightButtonClick = () => {
    dispatch({ type: `ANIMATION_FIGHT_START_CANCEL` });
    setTimeout(() => {
      dispatch({ type: `FIGHT_END` }); //timer 300ms to match with transition on css and let animation finish before components switch
    }, 300);
  };

  return (
    <div onClick={onEndFightButtonClick} className={mc.container}>
      FIGHTING SCENE
    </div>
  );
};

export default FightScene;
