import mc from "./logs-menu.module.scss";

const LogsMenu = ({ logs, player1, player2 }) => {
  return (
    <ul className={mc.container}>
      {logs.split(" ").map((el, i) => (
        <li key={i} className={mc.log}>
          <span
            style={{
              color:
                el === player1.pseudo
                  ? player1.playerClass.color
                  : el === player2.pseudo
                  ? player2.playerClass.color
                  : "",
            }}
          >
            {el}
          </span>
        </li>
      ))}
    </ul>
  );
};

export default LogsMenu;
