import mc from "./fight-scene.module.scss";
import { useDispatch } from "react-redux";
import { render } from "react-dom";
import { Stage, Layer, Rect, Text } from "react-konva";
import Konva from "konva";
import { useState } from "react";
//sprites//
// import zombie_player1 from "../../images/zombie_player1.png";
// import zombie_player2 from "../../images/zombie_player2.png";
// import knight_player1 from "../../images/knight_player1.png";
// import knight_player2 from "../../images/knight_player2.png";
// import ninja_player1 from "../../images/ninja_player1.png";
// import ninja_player2 from "../../images/ninja_player2.png";

const FightScene = () => {
  const dispatch = useDispatch();

  const onEndFightButtonClick = () => {
    dispatch({ type: `ANIMATION_FIGHT_START_CANCEL` });
    setTimeout(() => {
      //setTimeout to match with transition on css and let animation finish before components switch
      dispatch({ type: `FIGHT_END` });
    }, 300);
  };

  const ColoredRect = () => {
    const [color, setColor] = useState("green");
    const handleClick = () => {
      setColor(Konva.Util.getRandomColor());
    };
    return (
      <Rect
        x={0}
        y={20}
        width={50}
        height={50}
        fill={color}
        shadowBlur={3}
        onClick={handleClick}
      />
    );
  };

  return (
    <div
      // onClick={onEndFightButtonClick}
      className={mc.container}
    >
      <Stage width={window.innerWidth} height={window.innerHeight}>
        <Layer>
          <Text text="Try click on rect" />
          <ColoredRect />
        </Layer>
      </Stage>
    </div>
  );
};

export default FightScene;
