import mc from "./pseudo-inputs.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";

const PseudoInputs = ({ labelContent, inputValue, player }) => {
  const dispatch = useDispatch();
  const { player1, player2 } = useSelector((store) => {
    return store.playersReducer;
  });
  const updatePseudo = (player, value) => {
    dispatch({ type: "PLAYER_PSEUDO", payload: { player, value } });
  };

  //Animation input test//

  // const [p1isHidden, setP1IsHidden] = useState(false);
  // const [p2isHidden, setP2IsHidden] = useState(false);

  // const handleAnimationEnd = (setHidden) => {
  //   setHidden(true);
  // };

  // const enterValidation = () => {
  //   handleAnimationEnd();
  // };

  return (
    <div className={mc.container}>
      <label className={mc.label}>{labelContent}</label>
      <input
        className={mc.input}
        required={true}
        type="text"
        name="pseudo"
        autoComplete="off"
        value={inputValue}
        onChange={(e) => updatePseudo(player, e.target.value)}
        // onKeyDown={enterValidation}
        // onAnimationEnd={() => handleAnimationEnd(setP1IsHidden)}
      ></input>
      <div className={mc.underline}></div>
    </div>
  );
};

export default PseudoInputs;
