import mc from "./fight-player-menu.module.scss";
import { useEffect, useState } from "react";
//images//
import rogueImgP1 from "../../../images/rogueImgP1.png";
import mageImgP1 from "../../../images/mageImgP1.png";
import warriorImgP1 from "../../../images/warriorImgP1.png";
import rogueImgP2 from "../../../images/rogueImgP2.png";
import mageImgP2 from "../../../images/mageImgP2.png";
import warriorImgP2 from "../../../images/warriorImgP2.png";
//images fight icons//
//attacks//
import mageAtk from "../../../images/atks/mageAtk.jpeg";
import rogueAtk from "../../../images/atks/rogueAtk.jpeg";
import warriorAtk from "../../../images/atks/warriorAtk.jpeg";
//defense//
import shield from "../../../images/atks/shield.jpeg";
//utltims//
import mageUltim from "../../../images/atks/mageFreeze.jpeg";
import rogueUltim from "../../../images/atks/pickpocketRogue.jpeg";
import warriorUltim from "../../../images/atks/warriorStun.jpeg";
//potions//
import potion from "../../../images/atks/potion.jpeg";

const FightPlayerMenu = ({
  playerNumber,
  player,
  onAttackClick,
  onDefenseClick,
  onUltimClick,
  onPotionClick,
}) => {
  const [img, setImg] = useState();

  useEffect(() => {
    if (playerNumber === 1) {
      if (player.playerClass.className === "voleur") {
        setImg(rogueImgP1);
      } else if (player.playerClass.className === "mage") {
        setImg(mageImgP1);
      } else {
        setImg(warriorImgP1);
      }
    } else if (playerNumber === 2) {
      if (player.playerClass.className === "voleur") {
        setImg(rogueImgP2);
      } else if (player.playerClass.className === "mage") {
        setImg(mageImgP2);
      } else {
        setImg(warriorImgP2);
      }
    }
  }, [
    playerNumber,
    player,
    rogueImgP1,
    mageImgP1,
    warriorImgP1,
    rogueImgP2,
    mageImgP2,
    warriorImgP2,
  ]);

  return (
    <div className={mc.container}>
      <div className={mc.player_menu}>
        <div>
          <button
            className={mc.attack}
            onClick={() => onAttackClick(playerNumber)}
          >
            <span className={mc.tooltip_content}>
              Inflige des dégâts MODÉRÉS à votre adversaire
            </span>
            <img
              src={
                player.playerClass.className === "voleur"
                  ? rogueAtk
                  : player.playerClass.className === "mage"
                  ? mageAtk
                  : warriorAtk
              }
              alt={`{image ${player.playerClass.className}`}
            />
          </button>

          <button
            className={mc.defense}
            onClick={() => onDefenseClick(playerNumber)}
          >
            <span className={mc.tooltip_content}>
              A une grande chance d'ESQUIVER la prochaine attaque et une FAIBLE
              chance de contre-attaquer en infligeant d'IMPORTANTS dégâts
            </span>
            <img src={shield} alt="image d'un bouclier" />
          </button>

          <button
            className={mc.ultim}
            onClick={() => onUltimClick(playerNumber)}
          >
            <span className={mc.tooltip_content}>
              {`${
                player.playerClass.className === "voleur"
                  ? "A une grande chance de voler une POTION à votre adversaire (s'il en possède au moins 1) (RECHARGE=3tours)"
                  : player.playerClass.className === "mage"
                  ? "A une grande chance de geler votre adversaire, d'infliger des dégâts FAIBLES et l'empêcher de jouer pendant 2 TOURS. (RECHARGE=3tours)"
                  : "A une grande chance d'assomer votre adversaire, d'infliger des dégâts FAIBLES et l'empêcher de jouer pendant 2 TOURS. (RECHARGE=3tours)"
              }`}
            </span>
            <img
              src={
                player.playerClass.className === "voleur"
                  ? rogueUltim
                  : player.playerClass.className === "mage"
                  ? mageUltim
                  : warriorUltim
              }
              alt="image d'une potion de soin"
            />
          </button>

          <button
            className={mc.potion}
            onClick={() => onPotionClick(playerNumber)}
          >
            <span className={mc.tooltip_content}>
              Rend entre 10% et 20% points de vies
            </span>
            <img src={potion} alt="image d'une potion de soin" />
            <span className={mc.potions_counter}>
              {player.playerClass.potions}
            </span>
          </button>
        </div>
      </div>

      <div className={`${mc.player_character} adaptive-img-contain`}>
        <span>
          <img src={img} alt={`{image ${player.playerClass.className}`} />
        </span>
      </div>
    </div>
  );
};

export default FightPlayerMenu;
