import mc from "./class-selection.module.scss";
import { classes } from "../../../constants/classes";
import { useState } from "react";
//utils//
import { firstLetterUpper } from "../../../utils/firstLetterCapital";

const ClassSelection = ({ pseudo, onClick, playerClassSelection }) => {
  const [selectedClass, setSelectedClass] = useState(null);
  const [animationActive, setAnimationActive] = useState(false);
  return (
    <div className={mc.container}>
      <div className={mc.pseudo_validate}>
        <span>{firstLetterUpper(pseudo)}</span>
        <button onClick={onClick}>
          <i className="icon-edit-2-outline"></i>
        </button>
      </div>
      <div
        className={`${mc.class_selection} ${
          animationActive ? mc.animation_active : ""
        }`}
      >
        <ul>
          {classes.map((el, i) => (
            <li key={i} style={{ "--class-color": el.color }}>
              <button onClick={() => playerClassSelection(el)}>
                {firstLetterUpper(el.className)}
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ClassSelection;
