const INITIAL_USER = {
  id: 1,
  email: "",
  emailConfirm: "",
  password: "",
  passwordConfirm: "",
};

const userReducer = (state = INITIAL_USER, action) => {
  switch (action.type) {
    case "INPUT": {
      return { ...state, [action.payload.input]: action.payload.value };
    }

    default: {
      return state;
    }
  }
};

export default userReducer;
