import mc from "./fight-scene.module.scss";
import { useDispatch, useSelector } from "react-redux";
//components//
import LifeBar from "../fight-scene-elements/life-bar/LifeBar";
import { $CombinedState } from "redux";
//images//
import rogueIcon from "../../images/rogue_icon.jpeg";
import mageIcon from "../../images/mage_icon.jpeg";
import warriorIcon from "../../images/warrior_icon.jpeg";

const FightScene = () => {
  const dispatch = useDispatch();

  const { player1, player2 } = useSelector((store) => {
    return {
      player1: store.playersReducer.player1,
      player2: store.playersReducer.player2,
    };
  });

  //launch switch page animation//
  const onEndFightButtonClick = () => {
    dispatch({ type: `ANIMATION_FIGHT_START_CANCEL` });
    setTimeout(() => {
      dispatch({ type: `FIGHT_END` });
    }, 300);
  };

  return (
    <div onClick={onEndFightButtonClick} className={mc.container}>
      <div className={mc.fightingZone_zone}>
        <div className={mc.top_menu}>
          <div className={mc.life_bars_player1}>
            <LifeBar
              playerNumber={1} //determine row-reverse for player1
              player={player1}
            />
          </div>
          <div className={mc.round_rotation}></div>
          <div className={mc.life_bars_player2}>
            <LifeBar
              playerNumber={2} //determine row-reverse for player2
              player={player2}
            />
          </div>
        </div>
        <div className={mc.mdj_menu}></div>
        <div className={mc.fighting_menu}></div>
      </div>
    </div>
  );
};

export default FightScene;
