export const mdjWelcomeString1 =
  "Bienvenue dans l'arène ${player1} et ${player2} ! Êtes vous prêts à combattre ? La force ne suffira pas, il faudra faire preuve de réflexion ! ... ... Ils ont pas l'air malins ces deux là. Je vais encore passer un grand moment... Bref, cliquez sur l'écran pour commencer !";

export const mdjTauntAttacker = [
  "${attacker} devrait peut-être passer chez l'ophtalmo !",
  "${attacker} manquerait une vache dans un tout petit couloir avec bazooka !",
  "${defender} est aussi agile qu'un chat ! Ou ${attacker} est vraiment mauvais, je vous laisse en juger.",
  "${attacker} devrait peut-être se repprocher un peu de sa cible, on aurait une chance de voir un vrai combat...",
];

export const mdjTauntDefender = [
  "${defender} est lent... Oui ${defender} tu es lent ! Cette appli est superbement bien optimisée, il n'y a aucun lags ici !!",
  "L'attaque de ${attacker} était vraiment téléphonée, comment ${defender} peut ne pas esquiver ça...",
  "Hey ${defender} on se réveille, même ma grand mère l'aurait esquivée celle là !!!",
  "${defender} !! Parer avec sa tête c'est pas une tactique défensive très efficace...",
];
