import mc from "./winnner.module.scss";

const Winner = ({ player, onEndFightButtonClick, bckImage }) => {
  return (
    <div
      className={mc.container}
      //   style={{ backgroundImage: `url(${bckImage})` }}
    >
      <div>
        <h3>
          <span style={{ color: `${player.playerClass.color}` }}>
            {player.pseudo.toUpperCase()}
          </span>
          <span>remporte le match !</span>
        </h3>
        <button onClick={onEndFightButtonClick}>OK</button>
      </div>
    </div>
  );
};

export default Winner;
