const INITIAL_STATE = {
  player1: {
    pseudo: "",
    playerClass: {
      className: "",
      hp: null,
      atk: null,
      int: null,
      spd: null,
      agi: null,
      armor: null,
      color: "",
      icon: "",
      potions: null,
    },
  },
  player2: {
    pseudo: "",
    playerClass: {
      className: "",
      hp: null,
      atk: null,
      int: null,
      spd: null,
      agi: null,
      armor: null,
      color: "",
      icon: "",
      potions: null,
    },
  },
};

const playersReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "PLAYER_PSEUDO": {
      return {
        ...state,
        [action.payload.player]: {
          ...state[action.payload.player],
          pseudo: action.payload.pseudo,
        },
      };
    }

    case "UPDATE_PLAYER_CLASS": {
      return {
        ...state,
        [action.payload.player]: {
          ...state[action.payload.player],
          playerClass: action.payload.playerClass,
        },
      };
    }

    // case "PLAYER_TAKE_DMG": {
    //   const { player, hitValue } = action.payload;
    //   return {
    //     ...state,
    //     [action.payload.player]: {
    //       ...state[player],
    //       playerClass: {
    //         ...state.playerClass,
    //         hp: state.hp - hitValue,
    //       },
    //     },
    //   };
    // }

    case "PLAYER_TAKE_DMG": {
      const { player, hitValue } = action.payload;
      const playerData = state[player];

      if (playerData && playerData.playerClass && playerData.playerClass.hp) {
        // Vérifiez si playerData et playerData.playerClass existent
        // avant d'accéder à la propriété hp
        const newHP = playerData.playerClass.hp - hitValue;

        return {
          ...state,
          [player]: {
            ...state[player],
            playerClass: {
              ...state[player].playerClass,
              hp: newHP,
            },
          },
        };
      }
    }

    default: {
      return state;
    }
  }
};

export default playersReducer;
