const INITIAL_STATE = {
  playersClickOnstartGame: false,
  startTv: false,
  round: 0,
  dice: {
    isOn: true,
    value: null,
  },
};

const fightReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "LAUNCH_FIGHT": {
      return { ...state, playersClickOnstartGame: true };
    }
    case "START_TV": {
      return { ...state, startTv: true };
    }
    case "UPDATE_ROUND": {
      return { ...state, round: action.payload.round };
    }
    case "UPDATE_DICE": {
      return {
        ...state,
        dice: { ...state.dice.value, value: action.payload.value },
      };
    }

    default: {
      return state;
    }
  }
};

export default fightReducer;
