import mc from "./players-menu.module.scss";
import { useSelector, useDispatch } from "react-redux";
//components//
import PseudoInputs from "../inputs/pseudoInputs/PseudoInputs";

const PlayersMenu = () => {
  const dispatch = useDispatch();
  const { player1, player2 } = useSelector((store) => {
    return store.playersReducer;
  });

  const { pseudoPlayer1Validate, pseudoPlayer2Validate } = useSelector(
    (store) => {
      return store.modificatorsReducer;
    }
  );

  const { theme } = useSelector((store) => {
    return {
      theme: store.themesReducer.theme,
    };
  });

  return (
    <div className={mc.container}>
      <div className={mc.players_pseudo}>
        <div className={mc.player1}>
          <PseudoInputs
            inputValue={player1.pseudo}
            player="player1"
            pseudoP1P2="Player1"
            // enterValidation={(e) => }
          />
        </div>
        <div className={mc.player2}>
          <PseudoInputs
            inputValue={player2.pseudo}
            player="player2"
            pseudoP1P2="Player2"
          />
        </div>
      </div>
    </div>
  );
};

export default PlayersMenu;
