const INITIAL_USER = {
  id: 1,
  email: "",
  emailConfirm: "",
  password: "",
  passwordConfirm: "",
};

const userReducer = (state = INITIAL_USER, action) => {
  switch (action.type) {
    case "UPDATE_INPUT": {
      return {
        ...state,
        [action.payload.input]: action.payload.value,
      };
    }

    case "LABEL_TOP": {
      return { ...state, ...action.payload.input, selected: true };
    }

    case "LABEL_DOWN": {
      return { ...state, ...action.payload.input, selected: false };
    }

    default: {
      return state;
    }
  }
};

export default userReducer;
